mapper.update:
UPDATE analysis_task
SET status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE analysis_task
SET analysis_result = #{analysisResult}, status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE analysis_task
SET error_message = #{errorMessage}, status = #{status}, update_time = NOW()
WHERE id = #{id}

mapper.delete:
DELETE FROM analysis_task 
WHERE status = #{status} 
AND create_time < DATE_SUB(NOW(), INTERVAL 24 HOUR)
